$(function(){
	
	var countli = $(".caroufredsel_wrapper ul li").size()
	if(countli < 4){
		$(".caroufredsel_wrapper").find(".next").addClass("disable")
	}

	$(".a-modal-close").on('click', function() {
		CloseModalWindow()
	});
	$(document).keyup(function(event){
		if (event.keyCode == 27) {
			CloseModalWindow()
		}
	});

});

function CentriredModalWindow(ModalName){
	$(ModalName).css({"display":"block","opacity":0});
	var modalH = $(ModalName).height();
	var modalW = $(ModalName).width();
	$(ModalName).css({"margin-left":"-"+(parseInt(modalW)/2)+"px","margin-top":"-"+(parseInt(modalH)/2)+"px"})
}

function OpenModalWindow(ModalName){
	$(ModalName).animate({"opacity":1},300);	
	$("#bgmod").css("display","block");
}

function CloseModalWindow(){
	$("#bgmod").css("display","none");
	$(".modal").css({"opacity":1});
	$(".modal").animate({"opacity":0},300);
	setTimeout(function() { $(".modal").css({"display":"none"}); }, 500)	
}
			
function addToCompare(element, mode, text, deleteUrl) {    
    if (!element && !element.href) return;

    var href = element.href;
    var button = $(element);
			  
	button/*.unbind('click')*/.removeAttr("href");
	titleItem = $(element).parents(".R2D2").find(".item_title").attr('title')
	imgItem = $(element).parents(".R2D2").find(".item_img").attr('src');
	$('#addItemInCompare .item_title').text(titleItem);
	$('#addItemInCompare .item_img img').attr('src', imgItem);
	var ModalName = $('#addItemInCompare');
	CentriredModalWindow(ModalName);
	OpenModalWindow(ModalName);
	
	if (mode == 'list')
	{					
		var removeCompare = '<input type="checkbox" class="addtoCompareCheckbox"/ checked><span class="checkbox_text">'+text+'</span>';
		button.html(removeCompare);
		button.attr("href", deleteUrl);
		button.attr("onclick", "return deleteFromCompare(this, \'"+mode+"\', \'"+text+"\', \'"+href+"\');");	
	}
	else if (mode == 'detail' || mode == 'list_price')
	{
		var removeCompare = text;
    	//button.html(removeCompare);
    }

	if (href)
		$.get( href + '&ajax_compare=1&backurl=' + decodeURIComponent(window.location.pathname),
			$.proxy(
				function(data) {									   
					var compare = $("#compare");
					compare.html(data);
					
	                if (compare.css("display") == "none") {
					    compare.css({ "display": "block", "height": "0" });
					}
				}, button
			)
		);
	return false;
}

function deleteFromCompare(element, mode, text, compareUrl) {
    if (!element || !element.href) return;

    //var href = element.href;
    var button = $(element);
    var href = button.attr("href");    
    
	button.unbind('click').removeAttr("href");
	if (mode == 'list')
	{					
		var removeCompare = '<input type="checkbox" class="addtoCompareCheckbox"/><span class="checkbox_text">'+text+'</span>';
		button.html(removeCompare);
		button.attr("href", compareUrl);
		button.attr("onclick", "return addToCompare(this, \'"+mode+"\', \'"+text+"\', \'"+href+"\');");	
	}

	$.get( href + '&ajax_compare=1&backurl=' + decodeURIComponent(window.location.pathname) ,
		$.proxy(
			function(data) {									   
				var compare = $("#compare");   
				compare.html(data);
				if (compare.css("display") == "none") {
				    compare.css({ "display": "block", "height": "0" });
				}				
			}, button
		)
	);   
	return false;
}

function disableAddToCompare(element, mode, text, deleteUrl) {
	if (!element ) return;
	                             
    element = $(element);              
	var href = element.attr("href");      
    if (mode == 'list')
    {
		var removeCompare = '<input type="checkbox" class="addtoCompareCheckbox"/ checked><span class="checkbox_text">'+text+'</span>';
		element.html(removeCompare);
		element.attr("onclick", "return deleteFromCompare(this, \'"+mode+"\', \'"+text+"\', \'"+href+"\');");
		element.attr("href", deleteUrl);					
	}
	else if (mode == 'detail' || mode == 'list_price')
	{
		var removeCompare = text;
	    element
	        //.html(removeCompare)
	        .unbind('click').removeAttr("href");
	}    
    return false;
}     

function compareTable(counttd) {
	$(".table_compare").find("table").css("width",100+"%")
	$(".table_compare table tr:first").find("td").css("width","auto");
}

function deleteFromCompareTable(element) {
	var counttd = $(".table_compare table tr:first").find("td img").size()
	if(counttd == 1){
		var heightsum = $(".workarea").height()-$(".breadcrumbs").height()
		$(".table_compare").remove();
		$(".filtren.compare").remove();
		$(".deleteAllFromCompareLink").remove();
		$(".emptyListCompare").css("display","block");
		$(".sort").remove();
		$(".nocomapre").css({"height":heightsum+"px","display":"table","width":100+"%"},300);
	
	} else if(counttd < 4){
		compareTable(counttd);  
	}      
	var tdInd = $(element).parent('td').index();
	wtdc = $(element).parents('table').find("tr").find("td:nth("+tdInd+")").width()
	$(element).parents('table').find("tr").find("td:nth("+tdInd+")").css({"width":wtdc+"px","padding":0+"px"}); 
	$(element).parents('table').find("tr").find("td:nth("+tdInd+")").animate({"width":0+"px","padding":0+"px"}, 300); 
	$(element).parents('table').find("tr").find("td:nth("+tdInd+")").text('');
	$(element).parents('table').find("tr").find("td:nth("+tdInd+")").remove();       
	 
	var href = element.href;                 
	$.get( href);     

	return false;
}

function addToCart(el){

    var button  = $(el);
    var href    = button.attr('href');
    var mode    = button.data('mode');
    var text    = button.data('text');
    var type    = button.data('type');


	if (!href) return;

	//button.unbind('click').removeAttr("href");
 
	var titleItem   = button.parents(".R2D2").find(".data-item_title").text();
	var imgItem     = button.parents(".R2D2").find(".data-item_img").attr('src');

	$('#addItemInCart .item_title').text(titleItem);
	$('#addItemInCart .item_img img').attr('src', imgItem);
    $('#addItemInCart .hidden_id').val(button.parents('.items_item.content_list').data('id'));
	var ModalName = $('#addItemInCart');
	CentriredModalWindow(ModalName);
	OpenModalWindow(ModalName);

	if (href){

		$.get( href+"&ajax_buy=1", $.proxy(
	  		function(data){
				$(".header_cart_in").html(data);
			}, button)
		);

    }
	return false;
}

function disableAddToCart(elementId, mode, text) { 
	var	element = $("#"+elementId);
	if (!element)
		return;
    
	$(element).removeAttr("href");               

}

function DeleteFromCart(element) {
	$(element).parents('tr').animate({"height":0+"px","opacity":0,"overflow":"hidden","display":"none"}, 300);
	$(element).parents('tr').find("td").animate({"height":0+"px","padding":0+"px"}, 300);
	$(element).parents('tr').find("td").text('').remove();
	var href = element.href;  
	if (href)               
		$.get( href);     

	return false;
}




$(".horizontalfilter > li > span").on('click', function() {
	var ind = $(this).parent("li").index();
	ind++;
	if ($(this).parent("li").hasClass("active")){} else
	{	
		$(this).parents('.filtren.compare').find('.active').removeClass('active')
		$(this).parent("li").addClass('active');
		$(".filtren.compare .cntf").find(".cnt:nth-child("+ind+")").addClass('active');
	}
	return false;
}); 

 
$(document).ready(function() {
    $('body').on('click', '.addtoCart', function (e) {
        console.log(e);
        e.preventDefault();
        addToCart(this);
    });
});     

