        function initFlash(flash_name, flash_div, width, height){
            if(!flash_name || !flash_div){
                return false;
            }

            if(!width) width = '100%';
            if(!height) height = 70;

            var flashvars = {};
            var params = {};
            params.menu = "false";
            params.quality = "high";
            params.scale = "scale";
            params.wmode = "transparent";
            params.allowscriptaccess = "always";
            params.bgcolor = "#FFFFFF";
            var attributes = {};
            attributes.id = flash_name;

            swfobject.embedSWF(flash_name, flash_div, width, height, "8.0.0", "../flash/express_install.swf", flashvars, params, attributes);
        }

